export const movieDatabase = [
    {
        id: 1,
        title: "Interstellar",
        year: 2014,
        poster: "https://images.unsplash.com/photo-1440404653325-ab127d49abc1?w=300&h=450&fit=crop",
        genre: "Sci-Fi"
    },
    {
        id: 2,
        title: "The Godfather",
        year: 1972,
        poster: "https://images.unsplash.com/photo-1489599859333-5c0b6055e282?w=300&h=450&fit=crop",
        genre: "Crime"
    },
    {
        id: 3,
        title: "Inception",
        year: 2010,
        poster: "https://images.unsplash.com/photo-1518676590629-3dcbd9c5a5c9?w=300&h=450&fit=crop",
        genre: "Thriller"
    },
    {
        id: 4,
        title: "The Dark Knight",
        year: 2008,
        poster: "https://images.unsplash.com/photo-1509347528160-9329228ef454?w=300&h=450&fit=crop",
        genre: "Action"
    },
    {
        id: 5,
        title: "Pulp Fiction",
        year: 1994,
        poster: "https://images.unsplash.com/photo-1535016120720-40c646be5580?w=300&h=450&fit=crop",
        genre: "Crime"
    },
    // ... (continue with all 105 movies from the original file)
];

export const getRandomMovie = () => {
    const randomIndex = Math.floor(Math.random() * movieDatabase.length);
    return movieDatabase[randomIndex];
};

export const getMultipleRandomMovies = (count) => {
    const shuffled = [...movieDatabase].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, count);
};
